const messages = $input.all().map(m => m.json);
const messageHistory = messages.map(m => 
  `${m.sender_type}: ${m.content}`
).join('\n');

const customerMessages = messages.filter(m => m.sender_type === 'customer');
const messageCount = customerMessages.length;

// Calculate average response time
const responseTimes = [];
for (let i = 0; i < messages.length - 1; i++) {
  if (messages[i].sender_type === 'customer' && messages[i+1].sender_type === 'agent') {
    const diff = new Date(messages[i+1].created_at) - new Date(messages[i].created_at);
    responseTimes.push(diff / 1000 / 60); // minutes
  }
}
const avgResponseTime = responseTimes.length > 0 
  ? responseTimes.reduce((a, b) => a + b, 0) / responseTimes.length 
  : 0;

return {
  json: {
    conversation_id: $('New Message Received').item.json.conversation_id,
    contact_id: $('New Message Received').item.json.contact_id,
    message_history: messageHistory,
    message_count: messageCount,
    avg_response_time: Math.round(avgResponseTime),
    latest_message: messages[0].content
  }
};

